import React from 'react'
import {jwtDecode} from 'jwt-decode';
import Navbar from './Navbar';
import PasswordModal from './PasswordModal';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars } from '@fortawesome/free-solid-svg-icons';
import { useState, useEffect,useRef } from 'react';


export default function Profile() {
  const [isOpen, setIsOpen] = useState(false);
  const [username, setUsername] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const[photo, setPhoto] = useState(null);
  const handlePasswordChange = (data) => {
    console.log("Passwords submitted:", data);
    setIsModalOpen(false);
  };

   const fileInputRef = useRef(null);

    const handleClick = () => {
      fileInputRef.current.click(); 
    };
  
    const handleFileChange = async(e) => {
      console.log('File input changed');
      const file = e.target.files[0];
      console.log('Selected file:', file);
      const previewUrl = URL.createObjectURL(file);
      setPhoto(previewUrl);
      try{
        await axios.post('http://localhost:5000/api/adduserdetails', {
          username:username,
          photo: photo
        });
      }catch(err){
        console.error('failed to add details', err);
      }
    };

  const toggleMenu = () => setIsOpen((prev) => !prev);
  useEffect(()=>{
       const token = localStorage.getItem('token');
    if (token) {
      const decoded = jwtDecode(token);
      setUsername(decoded.username); 
    }
  },[]);
  return (
    <>
      <Navbar/>
      <div className='headers'>
        <div className='heading'>My profile</div>
      <button  onClick = {toggleMenu}className="menu-button">
         <FontAwesomeIcon icon={faBars} />
      </button>
      {isOpen && (
        <div className="dropdown">
          <div className="menu-item">Permissions</div>
          <div className="menu-item">Logout</div>
        </div>
      )}
      </div>
      <div className='profile-box'>
        <div className='profile-pic-box'>
        <div className="profile-pic-wrapper">
        <button  onClick={handleClick} className="edit-icon">ðŸ“·</button>
        <input type = "file" ref = {fileInputRef} onChange={handleFileChange} style={{display: 'none'}} accept = "image/*"></input>
        </div>
        </div>
        <div className="profile-info">
          <br></br>
        <p className="username">{username}</p>
      <button className='change-pwd' onClick={() => setIsModalOpen(true)}>Change password?</button>
      <PasswordModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onSubmit={handlePasswordChange}
      />
     </div>
      </div>
    </>  
  )
}
