import React from 'react';
import Navbar from './Navbar';
import {useState, useEffect} from 'react';

export default function Profile() {
  const [uname, setUname] = useState('');
  const [storedata, setStoredata] = useState({
    username: '',
    password: '',
    
    
  })
  const [formdata, setFormdata] = useState({
    username:'',
    oldpwd: '',
    newpwd:'',
    confirmpwd: ''
  });
  
  
  const handleInput=(e, field)=>{
      setFormdata({
        ...formdata, [field] : e.target.innerText
      });
  };
  
  const handleSubmit= (event)=>{
      
  };


  console.log(formdata);
   useEffect(()=>{
      const token = localStorage.getItem('token');
      console.log("Token:", token);
      if (token) {
      fetch("http://localhost:5000/profile", {
        method: "GET",
        headers: {
          "Authorization": `Bearer ${token}`
        }
      })
       .then((res) => {
        
    if (!res.ok) {
      throw new Error("Failed to fetch profile");
    }
    return res.json();
  })
  .then((data) => {
    console.log("Full response data:", data);

    console.log("Fetched username:", data.username);
    setUname(data.username);
  })
  .catch((err) => console.error("Fetch error:", err));
    }
   }, []);
  return(
    <>
       <Navbar/>
       <div className='profile-page'>
        <div className='profile-pic'>
          <div className='profile-photo'></div>
          <div>{uname}</div>
        </div>
        <div className='profile-info'>
          <div className='profile-heading'><b>Edit Profile</b></div>
          <form onSubmit = {handleSubmit}>
            <div className='edit-profile'>
            <label   className = 'edit-data'>Change username*</label>
            <div  data-field= 'username' onInput ={(e)=>handleInput(e, 'username')} contentEditable = {true} className='uname-div'> 
        </div><br></br>
            <label className='edit-data'>Old password*</label>
            <div data-field ='oldpwd' onInput ={(e)=>handleInput(e, 'oldpwd')} contentEditable = {true} className = 'uname-div'>
          </div><br></br>
            <label className='edit-data'>Change password*</label>
            <div  data-field = 'newpwd' onInput ={(e)=>handleInput(e, 'newpwd')}  contentEditable = {true} className = 'uname-div'>
          </div><br></br>
            <label className = 'edit-data'> enter password again*</label>
            <div  data-field = 'confirmpwd' onInput ={(e)=>handleInput(e, 'confirmpwd')}  contentEditable = {true}  className = 'uname-div' >
        </div>
          </div><br></br>
          <button type = "submit" className='submit-form'>Submit</button>
          </form>
          
        </div>
       </div>
    </>
  )
}
