import React from 'react';
import { useState} from 'react';
import Navbar from './components/Navbar';
// import Compone from './components/Compone';
// import Apron from './components/Apron';
// import Drafter from './components/Drafter';
// import Calci from './components/Calci';
// import Questionpaper from './components/Questionpaper';
// import Books from './components/Books';
// import Other from './components/Other';
// import Modal from './components/Modal';
import Maincomponent from './components/Maincomponent';

import './App.css';
// import {
//   BrowserRouter as Router,
//   Routes,
//   Route
// } from "react-router-dom";

function App() {
  // const [divs, setDivs] = useState([]);
  // const addDiv = () => { 
  //     const newDivs = ([...divs, `Div ${divs.length + 1}`]); 
  //     setDivs(newDivs);
  // };
   const [selected, setSelected] = useState(null);
   const items = [
    { type: 'Apron', title: 'Apron Title', description: 'Apron Description' },
    { type: 'Books', title: 'Book Title', description: 'Book Description' },
    { type: 'Calci', title: 'Book Title', description: 'Book Description' },
    { type: 'qp', title: 'Book Title', description: 'Book Description' },
    { type: 'drafter', title: 'Book Title', description: 'Book Description' },
    { type: 'Other', title: 'Book Title', description: 'Book Description' },
    // Add more items as needed
   ];
   const handleItemClick = (type) => {
    const item = items.find(i => i.type === type);
    setSelected(item);
   };


   return (
    <>
     <Navbar></Navbar>
     <div> 
     <div className = "buttons">
      <button className = "Apron" onclick = {handleItemClick}>Apron</button>
      <button className = "Books">Books</button>
      <button className = "Calci">Calci</button>
      <button className = "qp">Question Paper</button>
      <button className = "Drafter">Drafter</button>
      <button className = "other">Other</button>
     </div>
      {selected && <Maincomponent item={selected}/>} 
    </div>
    </>
  );
};


export default App;
