import React, { useState } from 'react';

export default function PasswordModal({ isOpen, onClose, onSubmit }) {
  const [prevPassword, setPrevPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [isModalOpen, setIsModalOpen] = useState(false);
  const handlePasswordChange = (data) => {
    console.log("Passwords submitted:", data);
    // Do your validation or API call here
    setIsModalOpen(false);
  };

  if (!isOpen) return null;

  const handleSubmit = () => {
    if (newPassword !== confirmPassword) {
      setError("New passwords do not match!");
      return;
    }
    setError('');
    // Pass data back to parent or handle submit here
    onSubmit({ prevPassword, newPassword, confirmPassword });
  };

  return (
    <div style={styles.overlay}>
      <div style={styles.modal}>
        <h2>Change Password</h2>

        <label>
          Previous Password:
          <input
            type="password"
            value={prevPassword}
            onChange={e => setPrevPassword(e.target.value)}
            style={styles.input}
          />
        </label>

        <label>
          New Password:
          <input
            type="password"
            value={newPassword}
            onChange={e => setNewPassword(e.target.value)}
            style={styles.input}
          />
        </label>

        <label>
          Confirm New Password:
          <input
            type="password"
            value={confirmPassword}
            onChange={e => setConfirmPassword(e.target.value)}
            style={styles.input}
          />
        </label>

        {error && <p style={{ color: 'red' }}>{error}</p>}

        <div style={styles.buttons}>
          <button onClick={handleSubmit}>Submit</button>
          <button onClick={onClose} style={{ marginLeft: '10px' }}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
}
