import React from 'react'
import { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faTimes } from '@fortawesome/free-solid-svg-icons';

export default function Modal({closeModal}) {
  const [data, setData] = useState({
    description: '', contactno: ''
  });
  const handleChange =(e) =>{
    const {name, value} = e.target;
    console.log(`Updating field: ${name} with value: ${value}`);
    setData((prev)=>{
      return {...prev, 
        [name]: value
      };
    })
  };
  const handleSubmit = (e)=>{
    e.preventDefault();
    console.log('form data:' ,data);
  };
  
  return (
    <div className= 'ModalBackground'>
      <div className='ModalContainer'>
      <div className='close'>
        <button onClick={()=> {closeModal(false)}}><FontAwesomeIcon icon={faTimes} />
      </button>
      </div>
      <h1>ADD ITEM</h1>
      
       <form onSubmit = {handleSubmit} className='Modal-content'>
        <h4>Add Photo:</h4>
        <button className='Upload'>Upload</button>
        <h4>Add Description:</h4>
        <textarea className= 'description' name='description'   onClick= {handleChange}  type = 'text'></textarea>
        <h4>Contact No:</h4>
        <input type='tel' name = 'contactno'  onClick= {handleChange} placeholder='enter your contact number' required></input><br></br><br></br>
        <button  className='Add'>Add</button>
       </form>
      </div>
    </div>
  )
}
