import React from 'react'
import {useState} from 'react';
import { useNavigate } from 'react-router-dom';

export default function Login({onLogin}) {
  const [userName, setUserName] =useState('');
  const  [password, setPassword] = useState('');
  const navigate = useNavigate();
  const handleLogin =(event)=>{
    event.preventDefault();
    onLogin(userName, password);
    navigate('/');

  }
  return (
    <div className='page'>
      <img className = "userimg" src="https://cdn-icons-png.flaticon.com/512/9187/9187604.png" alt = "logo of user"></img>
     < div className='login-box'>
      <form onSubmit={handleLogin}>
        <div className='uname'>
          <label>Username</label><br></br>
          <input type = "username" placeholder='enter username' value = {userName} onChange={(e)=> setUserName(e.target.value)}></input>
        </div>
        <div className='pwd'>
          <label>Password</label><br></br>
          <input type = "password" placeholder='enter password' value = {password} onChange = {(e)=> setPassword(e.target.value)}></input>
        </div >
        <button className= "login-btn" type = "submit">Login</button>
      </form>
    </div>  
    </div>
  )

}
