import React from 'react';
import axios from 'axios';
import Navbar from './Navbar';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPencilAlt } from "@fortawesome/free-solid-svg-icons";
import { useState, useEffect } from 'react';

export default function Profile() {
  const [uname, setUname] = useState('');
  // const [storedata, setStoredata] = useState({
  //   username: '',
  //   password: '',


  // })
  const [editable, setEditable] = useState(false);
  const [pwd, setPwd] = useState(false);
  // const [formdata, setFormdata] = useState({
  //   username: '',
  //   oldpwd: '',
  //   newpwd: '',
  //   confirmpwd: ''
  // });
  
  const handleEdit = ()=>{
      setEditable(true);
  };
  
  const handlePwd = () =>{
    setPwd(true);
  }


  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetch("http://localhost:5000/profile", {
        method: "GET",
        headers: {
          "Authorization": `Bearer ${token}`
        }
      })
        .then((res) => {

          if (!res.ok) {
            throw new Error("Failed to fetch profile");
          }
          return res.json();
        })
        .then((data) => {
          console.log("Fetched username:", data.username);
          setUname(data.username);
        })
        .catch((err) => console.error("Fetch error:", err));
    }
  }, []);
  return (
    <>
      <Navbar />
      <div className='profile-page'>
        <div className='profile-pic'>
          <div className='profile-photo'></div>
        </div>
        <div className='profile-info'>
          <div className='profile-heading'><b>Edit Profile</b></div>
          <label>Username:</label>
          <div className='uname-label'>
            <div>{uname}</div>
            <div>emailid:</div>
            <button onClick = {handleEdit} className='uname-btn'><FontAwesomeIcon icon={faPencilAlt} /></button>
            {editable ? ( <input className='edit-email'></input>):('none')}
          </div>
          <div classname= 'change-pwd'>
            <button onclick = {handlePwd} className = 'change-password'>Change</button>
          {pwd ? (<div className='style-pwd'><form >
              <label>old password</label><br></br>
              <input type='password'></input><br></br>
              <label>new password</label><br></br>
              <input type = 'password'></input> <br></br>
              <label>confirm new password</label><br></br>
              <input type = 'password'></input>
            </form></div>) : ('none')}
            
          </div>
            
        </div>
      </div>
    </>
  )
}
